    /*
     * Get rekening by id_rek
     */
    function get_rekening($id_rek)
    {
        return $this->db->get_where('rekening',array('id_rek'=>$id_rek))->row_array();
    }
        
    /*
     * Get all rekening
     */
    function get_all_rekening()
    {
        $this->db->order_by('id_rek', 'desc');
        return $this->db->get('rekening')->result_array();
    }
        
