     */
    function update_rekening($id_rek,$params)
    {
        $this->db->where('id_rek',$id_rek);
        return $this->db->update('rekening',$params);
    }
    
    /*
     * function to delete rekening
     */
    function delete_rekening($id_rek)
    {
        return $this->db->delete('rekening',array('id_rek'=>$id_rek));
    }
}
